import { Component } from '@angular/core';
import { ServiceService } from 'src/app/service.service';

@Component({
  selector: 'app-headline',
  templateUrl: './headline.component.html',
  styleUrls: ['./headline.component.scss'],
})
export class HeadlineComponent {

  email:string=sebastian
  constructor(private service: ServiceService) {}

  
  /**
   * Scrolls to the specified item by ID.
   *
   * @param {string} elementId - The ID of the item to scroll to.
   *
   */
  scrollTo(elementId: string): void {
    this.service.scrollTo(elementId);
  }
}
<div
  class="position-relative d-flex flex-column justify-content-center pb-5 z-n1"
>
  <span class="emailField" (click)="scrollTo('email')">
    sebastian.hass76@gmail.com
  </span>
  <div class="d-flex flex-column text-center fontHeadline">
    <span>Frontend</span>
    <span>Developer </span>
  </div>
  <div class="d-flex align-items-center justify-content-center">
    <div class="bg-black me-2 headlineUnderline"></div>
    <span class="fontName">Sebastian Haß</span>
  </div>
</div>
