import { Component } from '@angular/core';

@Component({
  selector: 'app-potfolio-headline',
  templateUrl: './potfolio-headline.component.html',
  styleUrls: ['./potfolio-headline.component.scss']
})
export class PotfolioHeadlineComponent {

}
<div class="d-flex flex-column align-items-center mt-5 mb-5">
  <span
    class="fontHover fontHeadline"
    >Portfolio</span
  >
  <span class="fontDescription"
    >This page is a sample of my work - please feel free <br />
    to try them out.</span
  >
</div>
