import { Component, HostListener } from '@angular/core';
import { ServiceService } from 'src/app/service.service';

@Component({
  selector: 'app-potfolio-join',
  templateUrl: './potfolio-join.component.html',
  styleUrls: ['./potfolio-join.component.scss', '../portfolio.component.scss'],
})
export class PotfolioJoinComponent {
  slide: boolean = false;
  // @HostListener('window:resize', ['$event'])

  // /**
  //  * Handles the "Window Resize" event and adjusts the behavior accordingly.
  //  * @param {Event} event - The Resize event object.
  //  *
  //  */
  // onWindowResize(event: Event) {
  //   const screenWidth = window.innerWidth;
  //   if (screenWidth < 880) {
  //     this.slide = true;
  //   } else {
  //     this.slide = false;
  //   }
  // }

  constructor(public service: ServiceService) {}

  // ngOnInt() {
  //   if (this.service.isTouchDevice()) {
  //     this.slide = false;
  //   }
  // }

  /**
   * Activates the display of the description.
   *
   */
  showDescription() {
    this.slide = true;
  }

  /**
   * Disables the display of the description.
   *
   */
  hideDescription() {
    window.innerWidth < 880 ? (this.slide = true) : (this.slide = false);
  }
}
<div
  class="mb-5"
  (mouseenter)="showDescription()"
  (mouseleave)="hideDescription()"
>
  <div class="position-relative d-flex justify-content-between w-100 mediaBox">
    <div class="w-50 order3 descriptionContainerBox">
      <span class="fontNumbers">01/05</span>
      <div class="descriptionContainer">
        <span [style.opacity]="service.isTouchDevice() ||slide ? '1' : '0.2'" class="fontHeadline mt-3"
          >Join</span
        >
        <span [style.opacity]="service.isTouchDevice() ||slide ? '1' : '0.2'" class="fontLanguage"
          >JavaScript | HTML | CSS</span
        >
        <span [style.opacity]="service.isTouchDevice()||slide ? '1' : '0.2'" class="fontDescription"
          >Task manager inspired by the Kanban System. Create and organize tasks
          using drag and drop functions, assign users and categories.
        </span>
      </div>
    </div>
    <div class="w-50 imgContainer order2">
      <img draggable="false"
        [style]="{
          filter: service.isTouchDevice()||slide ? 'grayscale(0)' : 'grayscale(1)',
          transform: service.isTouchDevice()||slide ? 'scale(1.1)' : 'scale(1)',
          transition: 'transform 300ms'
        }"
        src="./assets/img/project/join.png"
      />
    </div>
    <div [style.opacity]="service.isTouchDevice() ? '1' : '0'" class="imgBorderLeft"></div>
    <img draggable="false"
      [style.opacity]="!service.isTouchDevice() ? '1' : '0'"
      [class.arrowRotateLeft]="!service.isTouchDevice()"
      class="position-absolute arrow"
      src="./assets/img/arrow.png"
    />
    <div
      [class.showDescriptionLeft]="!service.isTouchDevice()"
      class="position-absolute bottom-0 d-flex mb-2 gap-5 buttonSlideLeft"
    >
      <div class="buttonContainer">
        <a
          class="buttonHover"
          href="https://github.com/HassSebastian/Join-privat-Abgabe"
          target="_blank"
          >GitHub</a
        >
      </div>
      <div class="buttonContainer">
        <a
          class="buttonHover"
          href="https://join.sebastian-hass.de/index.html"
          target="_blank"
        >
          Live
        </a>
      </div>
    </div>
  </div>
</div>
